import { Chart } from 'chart.js'
import React, { Component } from 'react'

Chart.defaults.font.size = 16;

let xAxisLabels = [];
for(let i = -100; i < 100; i++) {
  let label = i/100;
  xAxisLabels.push(label);
}

export default class Plot extends Component {


  componentDidMount() {
    let ctx = document.getElementById("plotCanvas").getContext("2d");
    let plot = new Chart(ctx, {
      type: "line",
      data : {
        labels: xAxisLabels,
        datasets: [{
          label: "f(x)=x³ + 5x² + 3x",
          borderColor: "lightblue",
          function: function(x) {return x*x*x + 5*(x*x) + 3*x;},
          data: []
        },
      {
        labels: [0.5],
        datasets: [{
          label: "Current approximation",
          borderColor: "black",
          data: [3]
        }]
      }]
      },
      plugins: [{
        beforeInit: function(chart) {
          let data = chart.config.data;

          for(let i = 0; i < data.labels.length; i++) {
            let f = data.datasets[0].function;
            let x = data.labels[i];
            let y = f(x);

            data.datasets[0].data.push(y);
          }
        }
    }],
    options: {
      elements: {
        point: {
          radius: 0
        }
      }
    }
    })
    plot.update();
  }


  render() {



    return (
      <canvas id="plotCanvas">

      </canvas>
    )
  }
}
