import { Chart } from 'chart.js'
import React, { Component } from 'react'
import { Line } from 'react-chartjs-2'
import chartProperties from './ChartProperties'
import annotationPlugin from 'chartjs-plugin-annotation';

Chart.register(annotationPlugin);


Chart.defaults.font.size = 16;
Chart.defaults.color = "#bbb";

const context = React.createContext();

let initialxAxisLabels = [];
for(let i = -50; i < 50; i++) {
  let label = i;
  initialxAxisLabels.push(label);
}

function setPolynomial(x) {
  return x*x*x + 5*(x*x) + 3*x + 5;
}

export default class Plot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      xAxisLabels: initialxAxisLabels,
      yData: [],
      //fn: "x^3 + 5x^2 + 3x + 2"
      fn: "x^3 + 3x^2 + 3x + 20", 
      curApprox: 2
    }

  }

  componentDidMount() {
  }


  render() {
    let data = {
      labels: this.state.xAxisLabels,
      // The function
      datasets: [{
        label: "f(x)=x³ + 5x² + 3x + 2",
        borderColor: "blue",
        function: this.state.fn,
        data: [],
      }, 
      // The function's tangent line
      {
        label: "Tangent",
        borderColor: "gray",
        function: this.state.fn, // The function is later manipulated in the plugin for it to be a tangent line
        data: [],
        curApprox: this.state.curApprox
      },
    ]
    }


    return (
      <Line options={chartProperties.options} data={data} plugins={chartProperties.plugins} />
    )
  }
}
