import { create, all, evaluate } from 'mathjs'

// math.js
const config = {
  epsilon: 1e-12,
  matrix: 'Matrix',
  number: 'number',
  precision: 64,
  predictable: false,
  randomSeed: null
}
const math = create(all, config)

let chartProperties = {
    options: {
        elements: {
          point: {
            radius: 0
          }
        },
        responsive: true,
        scales: {
          y: {
            min: -50,
            max: 250 
          },
          x: {
            min: -250,
            max: 500
          },
        },
        plugins: {
          autocolors: false,
          annotation: {
            annotations: {
              xAxis: {
                // Indicates the type of annotation
                type: 'line',
                yMin: 0,
                yMax: 0,
                borderColor: '#383838'
              }
            }
          }
        }
      },
      plugins: [{
        beforeInit: function(chart) {
          let data = chart.config.data;
          for(let j = 0; j < data.datasets.length; j++) {
            for(let i = 0; i < data.labels.length; i++) {
              let f = data.datasets[j].function;

              let x = data.labels[i];
              let y;

              y = math.evaluate(f, {x: x});
              if(data.datasets[j].label == "Tangent") {
                let a = data.datasets[j].curApprox;


                let derivative = math.derivative(f, "x")

                // L(x) = f '(a) (x - a) + f(a)
                let L = `${derivative.evaluate({x:a})} * (x-${a}) + ${evaluate(f, {x:a})}`;


                y = math.evaluate(L, {x:x});
              }


              data.datasets[j].data.push(y);
            }
          }
        },
        annotation: {
          annotations: {
            line1: {
              type: 'line',
              yMin: 10,
              yMax: 10,
              borderColor: 'white',
              borderWidth: 2,
            }
          }
        }
    }],

}

export default chartProperties;
