import { Chart } from 'chart.js'
import React, { Component } from 'react'
import { create, all, evaluate } from 'mathjs'
import { isLabelWithInternallyDisabledControl } from '@testing-library/user-event/dist/utils'
import { Line } from 'react-chartjs-2'
import chartProperties from './ChartProperties'

const config = {
  epsilon: 1e-12,
  matrix: 'Matrix',
  number: 'number',
  precision: 64,
  predictable: false,
  randomSeed: null
}
const math = create(all, config)
Chart.defaults.font.size = 16;
Chart.defaults.color = "#bbb";

const context = React.createContext();

let initialxAxisLabels = [];
for(let i = -50; i < 50; i++) {
  let label = i;
  initialxAxisLabels.push(label);
}

function setPolynomial(x) {
  return x*x*x + 5*(x*x) + 3*x + 5;
}

export default class Plot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      xAxisLabels: initialxAxisLabels,
      yData: [],
      //fn: "x^3 + 5x^2 + 3x + 2"
      fn: "x^3 + 3x^2 + 3x + 20", 
      curApprox: 2
    }

  }

  componentDidMount() {
    let ctx = document.getElementById("plotCanvas").getContext("2d");
    let plot = new Chart(ctx, {
      type: "line",
    annotation: {
      annotations: [
        {
          type: "line",
          mode: "vertical",
          scaleID: "x-axis-0",
          borderColor: "red",
          label: {
            content: "",
            enabled: true,
            position: "top"
          }
        }
      ]
    }
    })
    plot.update();
  }


  render() {



    return (
      <Line options={this.options} data={this.data} plugins={this.plugins}/>
    )
  }
}
