import { Chart } from 'chart.js'
import React, { Component } from 'react'
import { create, all } from 'mathjs'

const config = {
  epsilon: 1e-12,
  matrix: 'Matrix',
  number: 'number',
  precision: 64,
  predictable: false,
  randomSeed: null
}
const math = create(all, config)
Chart.defaults.font.size = 16;
Chart.defaults.color = "#bbb";

let initialxAxisLabels = [];
for(let i = -500; i < 500; i++) {
  let label = i;
  initialxAxisLabels.push(label);
}

function polynomial(x) {
  return x*x*x + 5*(x*x) + 3*x + 5;
}
function derivativeData(x,fn, xLabels) {
  let data = [];
  for(let i = 0; i < xLabels.length; i++) {
    let derivativeValue;
  }
}

export default class Plot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      xAxisLabels: initialxAxisLabels,
      yData: [],
      //fn: "x^3 + 5x^2 + 3x + 2"
      fn: "x^2"
    }
  }

  componentDidMount() {
    let ctx = document.getElementById("plotCanvas").getContext("2d");
    console.log(this);
    let plot = new Chart(ctx, {
      type: "line",
      data : {
        labels: this.state.xAxisLabels,
        // The function
        datasets: [{
          label: "f(x)=x³ + 5x² + 3x + 2",
          borderColor: "blue",
          function: this.state.fn,
          data: [],
        }, 
        // The function's tangent line
        {
          label: "Tangent",
          borderColor: "gray",
          function: this.state.fn, // The function is later manipulated in the plugin for it to be a tangent line
          data: []
        }
      ]
      },
      plugins: [{
        beforeInit: function(chart) {
          let data = chart.config.data;

          for(let j = 0; j < data.datasets.length; j++) {
            for(let i = 0; i < data.labels.length; i++) {

              let f = data.datasets[j].function;
              let x = data.labels[i];
              let y;
              y = math.evaluate(f, {x: x});
              if(data.datasets[j].label == "Tangent") {
                console.log(math.derivative(f, "x"))
                y = math.derivative(f, "x").evaluate({x:x}).toString() + ` * (${x}-${y}) + ${y}`;
                y = math.evaluate(y);
              }


              data.datasets[j].data.push(y);
            }
          }
        }
    }],
    options: {
      elements: {
        point: {
          radius: 0
        }
      },
      responsive: true,
      scales: {
        y: {
          min: -1000,
          max: 1000
        },
        x: {
          min: -1000,
          max: 1000
        }
      }
    }
    })
    plot.update();
  }


  render() {



    return (
      <canvas id="plotCanvas">

      </canvas>
    )
  }
}
