import React, { Component } from 'react'

export default class Menu extends Component {
    constructor(props) {
        super(props);
    }
  render() {
    return (
        <>
            <button onClick={this.props.nextIterationFn}>Next Step</button>
            <button 
            onClick={this.props.randomizePolynomialFn}
            disabled={this.props.randomizeDisabled}
            >Randomize Polynomial</button>
            <input placeholder={"Initial approximation"} onChange={this.props.setApproxFn}></input>
        </>
    )
  }
}
