export default class Plot extends Component {
  constructor(props) {
    super(props);
    let state = {
      xAxisLabels: initialxAxisLabels,
      yData: []
    }
  }

  componentDidMount() {
    let ctx = document.getElementById("plotCanvas").getContext("2d");
    let plot = new Chart(ctx, {
      type: "line",
      data : {
        labels: this.this.state.xAxisLabels,
        datasets: [{
          label: "f(x)=x³ + 5x² + 3x + 2",
          borderColor: "blue",
          function: polynomial,
          data: [],
        }
      ]
      },
      plugins: [{
        beforeInit: function(chart) {
          let data = chart.config.data;

          for(let i = 0; i < data.labels.length; i++) {
            let f = data.datasets[0].function;
            let x = data.labels[i];
            let y = f(x);

            data.datasets[0].data.push(y);
          }
        }
    }],
    options: {
      elements: {
        point: {
          radius: 0
        }
      },
      responsive: true
    }
    })
    plot.update();
  }


  render() {



    return (
      <canvas id="plotCanvas">

      </canvas>
    )
  }
}
