import { Chart } from 'chart.js'
import React, { Component } from 'react'
import { Line } from 'react-chartjs-2'
import chartProperties from './ChartProperties'



Chart.defaults.font.size = 16;
Chart.defaults.color = "#bbb";

const context = React.createContext();

let initialxAxisLabels = [];
for(let i = -50; i < 50; i++) {
  let label = i;
  initialxAxisLabels.push(label);
}

function setPolynomial(x) {
  return x*x*x + 5*(x*x) + 3*x + 5;
}

export default class Plot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      xAxisLabels: initialxAxisLabels,
      yData: [],
      //fn: "x^3 + 5x^2 + 3x + 2"
      fn: "x^3 + 3x^2 + 3x + 20", 
      curApprox: 2
    }

  }

  componentDidMount() {
    let ctx = document.getElementById("plotCanvas").getContext("2d");
    let plot = new Chart(ctx, {
      type: "line",

    })
    plot.update();
  }


  render() {



    return (
      <Line options={chartProperties.options} data={chartProperties.data} plugins={chartProperties.plugins} annotation={chartProperties.annotation}/>
    )
  }
}
