import { Chart } from 'chart.js'
import React, { Component } from 'react'
import { create, all, evaluate } from 'mathjs'
import { isLabelWithInternallyDisabledControl } from '@testing-library/user-event/dist/utils'
import { Line } from 'react-chartjs-2'

const config = {
  epsilon: 1e-12,
  matrix: 'Matrix',
  number: 'number',
  precision: 64,
  predictable: false,
  randomSeed: null
}
const math = create(all, config)
Chart.defaults.font.size = 16;
Chart.defaults.color = "#bbb";

const context = React.createContext();

let initialxAxisLabels = [];
for(let i = -50; i < 50; i++) {
  let label = i;
  initialxAxisLabels.push(label);
}

function setPolynomial(x) {
  return x*x*x + 5*(x*x) + 3*x + 5;
}

export default class Plot extends Component {
  constructor(props) {
    super(props);

    this.state = {
      xAxisLabels: initialxAxisLabels,
      yData: [],
      //fn: "x^3 + 5x^2 + 3x + 2"
      fn: "x^3 + 3x^2 + 3x + 20", 
      curApprox: 2
    }
    this.options = {
      elements: {
        point: {
          radius: 0
        }
      },
      responsive: true,
      scales: {
        y: {
          min: -50,
          max: 250
        },
        x: {
          min: -250,
          max: 500
        },
      }
    }
  }

  componentDidMount() {
    let ctx = document.getElementById("plotCanvas").getContext("2d");
    let plot = new Chart(ctx, {
      type: "line",
      data : {
        labels: this.state.xAxisLabels,
        // The function
        datasets: [{
          label: "f(x)=x³ + 5x² + 3x + 2",
          borderColor: "blue",
          function: this.state.fn,
          data: [],
        }, 
        // The function's tangent line
        {
          label: "Tangent",
          borderColor: "gray",
          function: this.state.fn, // The function is later manipulated in the plugin for it to be a tangent line
          data: [],
          curApprox: this.state.curApprox
        },
      ]
      },
      plugins: [{
        beforeInit: function(chart) {
          let data = chart.config.data;
          for(let j = 0; j < data.datasets.length; j++) {
            for(let i = 0; i < data.labels.length; i++) {
              let f = data.datasets[j].function;

              let x = data.labels[i];
              let y;

              y = math.evaluate(f, {x: x});
              if(data.datasets[j].label == "Tangent") {
                let a = data.datasets[j].curApprox;


                let derivative = math.derivative(f, "x")

                // L(x) = f '(a) (x - a) + f(a)
                let L = `${derivative.evaluate({x:a})} * (x-${a}) + ${evaluate(f, {x:a})}`;


                y = math.evaluate(L, {x:x});
              }


              data.datasets[j].data.push(y);
            }
          }
        }
    }],
    annotation: {
      annotations: [
        {
          type: "line",
          mode: "vertical",
          scaleID: "x-axis-0",
          borderColor: "red",
          label: {
            content: "",
            enabled: true,
            position: "top"
          }
        }
      ]
    }
    })
    plot.update();
  }


  render() {



    return (
      <Line options={options} data={data} />
    )
  }
}
