import { Chart } from 'chart.js'
import React, { Component } from 'react'
import { Line } from 'react-chartjs-2'
import chartProperties from './ChartProperties'
import annotationPlugin from 'chartjs-plugin-annotation';
import zoomPlugin from 'chartjs-plugin-zoom';
import { derivative, random } from 'mathjs';
import { create, all, evaluate } from 'mathjs'
import randomPolynomial from './randomPolynomial';
import TangentToGraph from './tangentLine';

// math.js
const config = {
  epsilon: 1e-12,
  matrix: 'Matrix',
  number: 'number',
  precision: 64,
  predictable: false,
  randomSeed: null
}
const math = create(all, config)

Chart.register(annotationPlugin);
Chart.register(zoomPlugin);

Chart.defaults.font.size = 16;
Chart.defaults.color = "#bbb";

let initialxAxisLabels = [];
for(let i = -50; i < 50; i++) {
  let label = i/10;
  initialxAxisLabels.push(label);
}

export default class Plot extends Component {
  constructor(props) {
    super(props);

    let initFunc = randomPolynomial();
    let initDerivative = derivative(initFunc, "x");
    this.state = {
      xAxisLabels: initialxAxisLabels,
      yData: [],
      //fn: "x^3 + 5x^2 + 3x + 2"
      fn: initFunc,
      derivative: initDerivative, 
      curApprox: 2,
      options: {
        elements: {
          point: {
            radius: 0
          }
        },
        responsive: true,
        scales: {
          y: {
            min: -50,
            max: 250 
          },
          x: {
            min: -250,
            max: 500
          },
        },
        // The x axis (Line at y=0)
        plugins: {
          autocolors: false,
          annotation: {
            annotations: {
              xAxis: {
                type: 'line',
                yMin: 0,
                yMax: 0,
                borderColor: '#383838'
              },
              point: {
                type: 'point',
                xValue: 1,
                yValue: 0,
                backgroundColor: 'rgba(255, 99, 132, 0.25)'
              }
            }
          },
          /*
          zoom: {
            zoom: {
              wheel: {
                enabled: true,
              },
              pan: {
                enabled: true,
                mode: "xy"
              },
              pinch: {
                enabled: true
              },
              mode: 'xy',
            }
          }
          */
        }
      }
    }

    this.chartRef = React.createRef();

    this.nextIteration = this.nextIteration.bind(this);
    this.randomizePolynomial = this.randomizePolynomial.bind(this);
    this.setFunction = this.setFunction.bind(this);
    this.setApprox = this.setApprox.bind(this);
  }

  setFunction(f) {
    let fprime = derivative(f, "x");

    // Update the chart
    let yData = [];
      let tangentData = [];
      for(let i = 0; i < this.state.xAxisLabels.length; i++) {
        let x = this.state.xAxisLabels[i];

        yData.push(math.evaluate(f, {x: x}));

        let a = this.state.curApprox;
        let L = TangentToGraph(f, a, this.state.derivative);
        tangentData.push(math.evaluate(L, {x:x}))

        this.setState({
          yData: yData,
          tangentData: tangentData,
          fn: f,
          derivative: fprime
        });

      }
  }
  randomizePolynomial() {
    this.setFunction(randomPolynomial());
  }
  setApprox(e) {
    this.setState({
      curApprox: e.target.value,
    })
  }
  nextIteration() {
    // nextApprox = curApprox - f(curApprox)/f'(curApprox)
    let curApprox = this.state.curApprox;
    let f = this.state.fn
    let nextApprox = 
        curApprox 
          -
        evaluate(f, {x:curApprox})
          /  
        evaluate(this.state.derivative.toString(), {x: curApprox});


        this.setState(prevState => ({
          options: {
            ...prevState.options,
            plugins: {
              ...prevState.options.plugins,           
              annotation: {                     
                ...prevState.options.plugins.annotation,   
                annotations: {
                  ...prevState.options.plugins.annotation.annotations,
                  curApprox: {
                    type: 'point',
                    xMin: curApprox,
                    xMax: curApprox,
                    yValue: evaluate(f, {x: curApprox}),
                    backgroundColor: 'rgba(255, 99, 132, 0.25)'
                  },
                  nextApprox: {
                    type: 'point',
                    xValue: nextApprox,
                    yValue: evaluate(f, {x: nextApprox}),
                    backgroundColor: 'rgba(255, 99, 132, 0.25)'
                  }
                }
              }
            }
          }
        }))

    this.setState({
      nextApprox: curApprox,
      curApprox: nextApprox
    });
    // Trigger a chart update
    this.setFunction(f);
  }

  componentDidMount() {

  }


  render() {
    let data = {
      labels: this.state.xAxisLabels,
      // The function
      datasets: [{
        label: this.state.fn,
        borderColor: "blue",
        function: this.state.fn,
        data: this.state.yData,
      }, 
      // The function's tangent line
      {
        label: "Tangent",
        borderColor: "gray",
        function: this.state.fn, // The function is later manipulated in the plugin for it to be a tangent line
        derivative: this.state.derivative,
        data: this.state.tangentData,
        curApprox: this.state.curApprox
      },
    ]
    }

    return (
      <>
        <Line ref={this.chartRef} plugins={this.state.plugins}
      options={chartProperties.options} data={data}/>

        <button onClick={this.nextIteration}>Next Iteration</button>
        <button onClick={this.randomizePolynomial}>Randomize Polynomial</button>
        <input placeholder={"Initial approximation"} onChange={this.setApprox}></input>
      </>
    )
  }
}
